krpano.action:
copy(titlename,name);
hotspot_animate();
ifnot(webvr.isenabled,
    addplugin(get(titlename));
    txtadd(plugin[get(titlename)].parent,'hotspot[',get(name),']');

    if(wz == r,
        set(plugin[get(titlename)].align,right);
        set(plugin[get(titlename)].edge,left);
    );
    if(wz == l,
        set(plugin[get(titlename)].align,left);
        set(plugin[get(titlename)].edge,right);
    );
    if(wz == t,
        set(plugin[get(titlename)].align,top);
        set(plugin[get(titlename)].edge,bottom);
        set(plugin[get(titlename)].y,get(titlemargin)); 
    );
    if(wz == m,
        set(plugin[get(titlename)].align,bottom);
        set(plugin[get(titlename)].edge,top);
    );

    if(device.flash,
        if(scale LT 1,
            txtadd(lshtml,'　',get(titlename),'　　',);
            , txtadd(lshtml,'　',get(titlename),'　',);
        );
        //set(plugin[get(titlename)].html,get(lshtml)); ,
        set(plugin[get(titlename)].html,get(htitle));
    );
    ,
    ifnot(hotspot[get(titlename)].htype ==="scene",
        removehotspot(get(titlename));
    );
);

if(htype ==="scene",
    set(plugin[get(titlename)].html,get(htitle));
    loadstyle(load_scene);
    set(plugin[get(titlename)].hlink,get(hlink));
    plugin[get(titlename)].loadstyle(txt_style|hotspot_title|load_scene);
);
if(htype ==="text",
    set(plugin[get(titlename)].html,get(htitle));
    loadstyle(load_text);
    plugin[get(titlename)].loadstyle(txt_style|hotspot_title|load_text);
);

if(htype ==="image",
    set(plugin[get(titlename)].html,get(htitle));
    loadstyle(load_image);
    plugin[get(titlename)].loadstyle(txt_style|hotspot_title|load_image);
);

if(htype ==="video",
    set(plugin[get(titlename)].html,get(htitle));
    loadstyle(load_video);
    plugin[get(titlename)].loadstyle(txt_style|hotspot_title|load_video);
);

if(htype === "rich",

    loadstyle(load_iframe);

    plugin[get(titlename)].loadstyle(txt_style|hotspot_title|load_iframe);
) ;

ifnot(htitle,
    set(plugin[get(titlename)].visible,false);
);

if(device.flash,
    set(plugin[get(titlename)].height,25);
);

set(plugin[get(titlename)].visible,false);

set(animClick,'');
txtadd(animClick,"xmlCall.animClickHandle('",get(hotspot[get(name)].htitle),"','",get(hotspot[get(name)].htype),"','",get(hotspot[get(name)].colorvalue),"','",get(hotspot[get(name)].showstyle),"','",get(name),"','",get(hotspot[get(name)].preview),"')");

jscall(get(animClick));
set(layer[skin_scroll_window].visible,false);
for(set(i,0), i LT hotspot.count, inc(i),
    if( hotspot[get(i)].index !== index AND (hotspot[get(i)].htype === 'video' || hotspot[get(i)].htype === 'image' || hotspot[get(i)].htype === 'text') ,

        set(hotspot[get(i)].visible, false);
    )
);

for(set(i,0), i LT hotspot.count, inc(i),
    if( hotspot[get(i)].htype === 'video' || hotspot[get(i)].htype === 'image' || hotspot[get(i)].htype === 'text' ,
        trace(hotspot[get(i)].name);
        set(hotspot[get(i)].visible, true);
    )
);

set(last_thumbsshow, get(thumbsshow));
set(last_ui_show, get(ui_show)); 

skin_hide_ui();
set(autorotate.enabled,false);
set(events[skin_events].onclick,"");
set(control.usercontrol, off);


trace("unlock_show_skin");
if(last_ui_show ===true,skin_show_ui(););

skin_showthumbs(get(last_thumbsshow));
set(autorotate.enabled,true);   
set(control.usercontrol, all);
set(events[skin_events].onclick,"skin_click();");

trace('name=',%1);
lock_hide_skin();
set(layer[textpanel_con].visible,true);

set(layer[textpanel_title].html,get(hotspot[%1].htitle));
set(layer[textpanel_body].html,get(hotspot[%1].hlink));
set(layer[textpanel_body_vert].html,get(hotspot[%1].hlink));     
set(layer[textpanel_body_hori].html,get(hotspot[%1].hlink));    
set(layer[textpanellike_txt].html,get(hotspot[%1].likecount));     


if(device.handheld,
    if(stagewidth GT stageheight, 
        resize_textpanel(true); 
        ,resize_textpanel(false);
    );
);

set(layer[textpanel_con].hotspotid, get(hotspot[%1].id));
set(layer[textpanel_con].hotspotname, get(hotspot[%1].name));
set(layer[textpanel_con].opentime, get(timertick));

set(sendlog_funcall,'');
txtadd(sendlog_funcall,
    'sendClickEvent("vr_text_click","click","text","' 
    , get(hotspot[%1].id)
    ,'","' ,get(hotspot[%1].name) ,'","' 
    , get(scene[get(xml.scene)].id) 
    ,'","' ,get(scene[get(xml.scene)].name) ,'",' 
    ,get(webvr.isenabled) 
    ,',0,0);' 
);
jscall(get(sendlog_funcall));  

set(checkTextHotspot_funcall,'');
txtadd(checkTextHotspot_funcall,'xmlCall.checkTextHotspot("'
    , get(hotspot[%1].id) ,'","'
    , get(hotspot[%1].name),'");');
jscall(get(checkTextHotspot_funcall));


trace("resize_textpanel begin");  
if(%1==null, set(hori,true);, set(hori,%1););        
trace("resize_textpanel begin, hori=", hori ); 

if(hori,
    trace("resize_textpanel heng" );
    set(layer[textpanel_title].y,"15%");

    set(layer[textpanel_body_vert].visible,false);
    set(layer[textpanel_body_hori].visible,true);  

    set(layer[textpanel_close_top].height,"20%");
    set(layer[textpanel_close_bottom].height,"40%");
    set(layer[textpanel_close_left].width,"30%");
    set(layer[textpanel_close_right].width,"30%");            
    ,
    trace("resize_textpanel shu" );
    set(layer[textpanel_title].y,"20%");

    set(layer[textpanel_body_vert].visible,true);
    set(layer[textpanel_body_hori].visible,fals); 

    set(layer[textpanel_close_top].height,"20%");
    set(layer[textpanel_close_bottom].height,"40%");
    set(layer[textpanel_close_left].width,"0");
    set(layer[textpanel_close_right].width,"0");   
); 
 trace("resize_textpanel done");

unlock_show_skin();
set(layer[textpanel_con].visible,false);
jscall("xmlCall.showTextBanner();");
sub(duration, timertick, layer[textpanel_con].opentime);
roundval(duration, duration, 0);


set(sendlog_funcall,'');        
txtadd(sendlog_funcall,
    'sendClickEvent("vr_hotspot_duration","click","text","' 
    , get(layer[textpanel_con].hotspotid)
    ,'","' ,get(layer[textpanel_con].hotspotname) ,'","' 
    , get(scene[get(xml.scene)].id) 
    ,'","' ,get(scene[get(xml.scene)].name) ,'",' 
    ,get(webvr.isenabled) 
    ,',0,'
    ,get(duration),');' 
);
jscall(get(sendlog_funcall));     
trace("hide_textpanel 1");


plugin[swiper].showgallery(get(hotspot[%1].id), 
    get(hotspot[%1].htitle), 
    get(hotspot[%1].hlink),
    get(hotspot[%1].name),0);    

set(sendlog_funcall,'');
txtadd(sendlog_funcall,
    'sendClickEvent("vr_image_click","click","text","' 
    , get(hotspot[%1].id)
    ,'","' ,get(hotspot[%1].name) ,'","","",' 
    ,get(webvr.isenabled) 
    ,',0,0);' 
);        

jscall(get(sendlog_funcall));


 txtadd(play_funcall,'xmlCall.playVideo("', 
    get(hotspot[%1].id), '","', 
    %1, '","', 
    get(hotspot[%1].htitle), '","', 
    get(hotspot[%1].hlink), '",', 
    get(device.handheld), ');'); 

 jscall(get(play_funcall));
 lock_hide_skin();  

 set(sendlog_funcall,'');

txtadd(sendlog_funcall,
    'sendClickEvent("vr_video_click","click","text","' 
    , get(hotspot[%1].id)
    ,'","' ,get(hotspot[%1].name) ,'","' 
    , get(scene[get(xml.scene)].id) 
    ,'","' ,get(scene[get(xml.scene)].name) ,'",' 
    ,get(webvr.isenabled) 
    ,',0,0);' 
);
jscall(get(sendlog_funcall));


unlock_show_skin();

set(sendlog_funcall,'');        
txtadd(sendlog_funcall,
    'sendClickEvent("vr_hotspot_duration","click","video","' 
    , get(hotspot[%1].id)
    ,'","' ,%1 ,'","' 
    , get(scene[get(xml.scene)].id) 
    ,'","' ,get(scene[get(xml.scene)].name) ,'",' 
    ,get(webvr.isenabled) 
    ,',0,'
    , %2,');' 
);
jscall(get(sendlog_funcall));     
trace("hide_textpanel 1");


if(device.flash,  
    for(set(i,0), i LT hotspot.count, inc(i), 
         if(hotspot[get(i)].htype==="video" OR hotspot[get(i)].htype==="image",  
            set(hotspot[get(i)].visible, false); 
        );
    ); 
);

trace(%0);

set(txthotspotclick_funcall,'');
txtadd(txthotspotclick_funcall,'xmlCall.likeTextHotspot("'
    , get(layer[textpanel_con].hotspotid) ,'","'
    , get(layer[textpanel_con].hotspotname),'");');
jscall(get(txthotspotclick_funcall));

trace(%0, " ok");
